package com.java;

public class Contrlstatements_Break {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name="VIVEK";
		switch(name){
		  case"ABIN":
			  System.out.println("He is Abin");
			  break;
		  case"VIVEK":
			  System.out.println("He is Vivek");
			  break;
		}

	}

}
